export default {
  props: ['page', 'tableUserSearch', 'tableFilesSearch'],
  data() {
    return {
      filterInput: ''
    }
  },
  methods: {
    tableSearch() {
      if (this.page == "showUser") {
        this.tableUserSearch(this.filterInput)

      }
      if (this.page == "showFiles") {
        this.tableFilesSearch(this.filterInput)
      }
      this.filterInput = ''
    }
  }
}
